<?php

function reward_dice_add_settings_page()
{
    add_menu_page(
        'Reward Dice', // Name of the page.
        'Reward Dice', // Menu text.
        'manage_options', // User role they need to view the page.
        'reward-dice', // Slug for the admin url.
        'reward_dice_settings_page_markup', // Callback function that adds content to the page.
        'data:image/svg+xml;base64,', // 'dashicons-wordpress-alt', // Icon used, dashicons are a wp thing...or the url to your own icon...
        100 // Order/priority in the menu.
    );
}

add_action( 'admin_menu', 'reward_dice_add_settings_page' );

function reward_dice_settings_page_markup()
{
    if (!current_user_can('manage_options')) {
        return;
    }
    include( REWARD_DICE_PLUGIN_DIR . 'templates/admin/settings-page.php');
}
